document.addEventListener("DOMContentLoaded",()=>{window.addEventListener("scroll",function(){const o=window.scrollY,e=document.querySelector(".header-sticky"),t=document.getElementById("back-top");o<400?(e&&e.classList.remove("sticky-bar"),t&&(t.style.display="none")):(e&&e.classList.add("sticky-bar"),t&&(t.style.display="block"))});const c=document.querySelector("#back-top a");c&&c.addEventListener("click",function(o){o.preventDefault(),window.scrollTo({top:0,behavior:"smooth"})})});
